import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CommentList() {
    const [comments, setComments] = useState({});

    const onFetchComments = async () => {
        const res = await axios.get(`http://localhost:5000/api/posts/${postId}/comments`);
        setComments(res.data)
    }

    useEffect(() => {
        onFetchComments();
    }, []);

    const renderComments = Object.values(comments).map((comment) => {
        return (
            <div className="card"
                style={{ width: "30px", marginBottom: "20px" }}
                key={comment.id}>
                <div className="card-boyd">
                    <h3>{comment.content}</h3>
                </div>
            </div>
        )
    })
    return (
        <div>
            {renderComments}
        </div>
    );
}

export default CommentList;
